import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ProduitService } from 'src/app/services/produit.service';


@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  chercheProduit!: string;

  constructor(private produitService: ProduitService, private router: Router) {}

  chercher(){
    this.produitService.setData(this.chercheProduit);
    this.router.navigate(['search/' + `${this.chercheProduit}`]);
  }

}

    <div class="nav_bar">

        <div class="image">
            <img routerLink="" src="assets/off-logo-horizontal-light.svg" alt="Off logo">
        </div>

        <div class="bar_recherche" >
          <input  type="text" [(ngModel)]="chercheProduit" placeholder="Chercher un produit" (keyup.enter)="chercher()"  >
          <button  (click)="chercher()" ></button>
        </div>

    </div>
